components/common/footer:
{{#if banners.bottom}}
    {{#if settings.data_tag_enabled}}
        <div class="banners" data-banner-location="bottom">
            {{#each (limit banners.bottom_metadata 1)}}
                <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                    <div data-event-type="promotion-click">
                        {{{this.content}}}
                    </div>
                </div>
            {{/each}}
        </div>
    {{else}}
        <div class="banners" data-banner-location="bottom">
            <div class="banner">
                {{{limit banners.bottom 1}}}
            </div>
        </div>
    {{/if}}
{{/if}}

{{#if settings.show_newsletter_box}}
    {{#if theme_settings.halo_subscription_form}}
        {{> components/halothemes/home/home-subscription-form}}
    {{/if}}
{{/if}}

{{{region name="home_instagram_block--global"}}}
{{{region name="home_instagram_block"}}}

<footer class="footer" role="contentinfo">
    <div class="container">
        <section class="footer-info{{#all (if theme_settings.footer_info_logo) (if theme_settings.footer-1-heading) (if theme_settings.footer-2-heading) (if theme_settings.footer-3-heading) (if theme_settings.footer-4-heading)}} footer-info-show-all{{else}} footer-info-no-show-all{{/all}}">
            {{#if theme_settings.footer_info_logo}}
                <article class="footer-info-col footer-info-col--small halo-fadeInUp" data-halo-animate="1" data-section-type="storeInfo">
                    {{#if theme_settings.footer_info_logo}}
                        <div class="footer-info__logo">
                            <a href="{{urls.home}}">
                                <img src="https://cdn11.bigcommerce.com/s-{{settings.store_hash}}/product_images/uploaded_images/{{theme_settings.footer_info_logo}}" alt="Logo">
                            </a>
                        </div>
                    {{/if}}
                    <address>{{nl2br settings.address}}</address>

                    {{#if theme_settings.info_email_support}}
                        <p class="footer-info__phone"><a href="mailto:{{theme_settings.info_email_support}}"><span>{{theme_settings.info_email_support}}</span></a></p>
                    {{/if}}

                    {{#if settings.phone_number}}
                        <p class="footer-info__phone phone_number"><a href="tel:{{settings.phone_number}}"><span>{{settings.phone_number}}</span></a></p>
                    {{/if}}

                    {{#if theme_settings.footer_info_text_support}}
                        <p class="footer-info__text_sp">{{theme_settings.footer_info_text_support}}</p>
                    {{/if}}

                    {{#if theme_settings.show_social-links}}
                        <div class="footer-info-col--social">
                            {{> components/common/social-links}}
                        </div>
                    {{/if}}


                </article>
            {{/if}}

            {{#if theme_settings.footer-1-heading}}
                <article class="footer-info-col footer-info-col--small halo-fadeInUp" data-halo-animate="2" data-section-type="footer-categories">
                    <h3 class="footer-info-heading footer-info-heading--toggle">{{theme_settings.footer-1-heading}}<svg xmlns="http://www.w3.org/2000/svg" width="18" height="10" viewBox="0 0 18 10" fill="none"><path d="M17 1L9 9L0.999999 1" stroke="white" stroke-width="1.25" stroke-linejoin="bevel"/></svg></h3>
                    <ul class="footer-info-list">
                        {{#each (limit categories theme_settings.categories_show_footer_limit)}}
                            <li>
                                <a href="{{url}}"><span>{{name}}</span></a>
                            </li>
                        {{/each}}
                    </ul>
                </article>
            {{/if}}

            {{#if theme_settings.footer-2-heading}}
                <article class="footer-info-col footer-info-col--small halo-fadeInUp" data-halo-animate="3" data-section-type="footer-webPages">
                    <h3 class="footer-info-heading footer-info-heading--toggle">{{theme_settings.footer-2-heading}}<svg xmlns="http://www.w3.org/2000/svg" width="18" height="10" viewBox="0 0 18 10" fill="none"><path d="M17 1L9 9L0.999999 1" stroke="white" stroke-width="1.25" stroke-linejoin="bevel"/></svg></h3>
                    <ul class="footer-info-list">
                        {{#if theme_settings.footer-2-text1}}
                            <li><a href="{{theme_settings.footer-2-link1}}"><span>{{theme_settings.footer-2-text1}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-2-text2}}
                            <li><a href="{{theme_settings.footer-2-link2}}"><span>{{theme_settings.footer-2-text2}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-2-text3}}
                            <li><a href="{{theme_settings.footer-2-link3}}"><span>{{theme_settings.footer-2-text3}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-2-text4}}
                            <li><a href="{{theme_settings.footer-2-link4}}"><span>{{theme_settings.footer-2-text4}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-2-text5}}
                            <li><a href="{{theme_settings.footer-2-link5}}"><span>{{theme_settings.footer-2-text5}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-2-text6}}
                            <li><a href="{{theme_settings.footer-2-link6}}"><span>{{theme_settings.footer-2-text6}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-2-text7}}
                            <li><a href="{{theme_settings.footer-2-link7}}"><span>{{theme_settings.footer-2-text7}}</span></a></li>
                        {{/if}}
                        {{#if settings.gift_certificates_enabled}}
                            <li>
                                <a href="{{urls.gift_certificate.purchase}}"
                                   aria-label="{{lang 'common.gift_cert'}}"
                                >
                                    {{lang 'common.gift_cert'}}
                                </a>
                            </li>
                        {{/if}}
                    </ul>
                </article>
            {{/if}}

            {{#if theme_settings.footer-3-heading}}
                <article class="footer-info-col footer-info-col--small halo-fadeInUp" data-halo-animate="4" data-section-type="footer-webPages">
                    <h3 class="footer-info-heading footer-info-heading--toggle">{{theme_settings.footer-3-heading}}<svg xmlns="http://www.w3.org/2000/svg" width="18" height="10" viewBox="0 0 18 10" fill="none"><path d="M17 1L9 9L0.999999 1" stroke="white" stroke-width="1.25" stroke-linejoin="bevel"/></svg></h3>
                    <ul class="footer-info-list">
                        {{#if theme_settings.footer-3-text1}}
                            <li><a href="{{theme_settings.footer-3-link1}}"><span>{{theme_settings.footer-3-text1}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-3-text2}}
                            <li><a href="{{theme_settings.footer-3-link2}}"><span>{{theme_settings.footer-3-text2}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-3-text3}}
                            <li><a href="{{theme_settings.footer-3-link3}}"><span>{{theme_settings.footer-3-text3}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-3-text4}}
                            <li><a href="{{theme_settings.footer-3-link4}}"><span>{{theme_settings.footer-3-text4}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-3-text5}}
                            <li><a href="{{theme_settings.footer-3-link5}}"><span>{{theme_settings.footer-3-text5}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-3-text6}}
                            <li><a href="{{theme_settings.footer-3-link6}}"><span>{{theme_settings.footer-3-text6}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-3-text7}}
                            <li><a href="{{theme_settings.footer-3-link7}}"><span>{{theme_settings.footer-3-text7}}</span></a></li>
                        {{/if}}
                    </ul>
                </article>
            {{/if}}

            {{#if theme_settings.footer-4-heading}}
                <article class="footer-info-col footer-info-col--small halo-fadeInUp" data-halo-animate="5" data-section-type="footer-brands">
                    <h3 class="footer-info-heading footer-info-heading--toggle">{{theme_settings.footer-4-heading}}<svg xmlns="http://www.w3.org/2000/svg" width="18" height="10" viewBox="0 0 18 10" fill="none"><path d="M17 1L9 9L0.999999 1" stroke="white" stroke-width="1.25" stroke-linejoin="bevel"/></svg></h3>
                    <ul class="footer-info-list">
                        {{#if theme_settings.footer-4-text1}}
                            <li><a href="{{theme_settings.footer-4-link1}}"><span>{{theme_settings.footer-4-text1}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-4-text2}}
                            <li><a href="{{theme_settings.footer-4-link2}}"><span>{{theme_settings.footer-4-text2}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-4-text3}}
                            <li><a href="{{theme_settings.footer-4-link3}}"><span>{{theme_settings.footer-4-text3}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-4-text4}}
                            <li><a href="{{theme_settings.footer-4-link4}}"><span>{{theme_settings.footer-4-text4}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-4-text5}}
                            <li><a href="{{theme_settings.footer-4-link5}}"><span>{{theme_settings.footer-4-text5}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-4-text6}}
                            <li><a href="{{theme_settings.footer-4-link6}}"><span>{{theme_settings.footer-4-text6}}</span></a></li>
                        {{/if}}
                        {{#if theme_settings.footer-4-text7}}
                            <li><a href="{{theme_settings.footer-4-link7}}"><span>{{theme_settings.footer-4-text7}}</span></a></li>
                        {{/if}}
                    </ul>
                </article>
            {{/if}}
        </section>
    </div>

    <div class="footer-bottom">
        <div class="container footer-bottom__container{{#if theme_settings.show_copyright_footer_2}} has-item-center{{/if}}">
            <div class="footer-bottom__left" data-halo-animate="7">
                {{#if theme_settings.show_copyright_footer}}
                    <div class="footer-copyright copyright-pc">
                        <p class="powered-by">{{lang 'footer.copyright'}} &copy; {{moment format="YYYY"}} {{settings.store_name}}{{lang 'footer.dot'}}</p>
                    </div>
                {{/if}}
                {{#if theme_settings.show_powered_by}}
                    <div class="footer-copyright">
                        {{{theme_settings.powered_by-text}}}
                    </div>
                {{/if}}
            </div>
            <div class="footer-bottom__right" data-halo-animate="8">
                {{#if settings.paypal_commerce_credit_message}}
                <div class="footer-copyright">
                    <p class="paypal-credit">{{nl2br settings.paypal_commerce_credit_message}}</p>
                </div>
                {{/if}}
                {{#if theme_settings.show_payments_footer}}
                    {{> components/common/payment-icons}}
                {{/if}}
                {{#if theme_settings.show_copyright_footer}}
                    <div class="footer-copyright copyright-mb">
                        <p class="powered-by">{{lang 'footer.copyright'}} &copy; {{moment format="YYYY"}} {{settings.store_name}}{{lang 'footer.dot'}}</p>
                    </div>
                {{/if}}
            </div>
        </div>
    </div>
    {{{region name="ssl_site_seal--global"}}}
</footer>
{{#if theme_settings.halo_back_to_top}}
    <div id="haloBackToTop" class="halo-back-to-top">
        <a href="#">
            <svg xmlns="http://www.w3.org/2000/svg" width="15" height="9" viewBox="0 0 15 9" fill="none">
                <path fill-rule="evenodd" clip-rule="evenodd" d="M7.49999 1.9185L1.45798 8.42527L0.541992 7.57471L7.04199 0.574707H7.95798L14.458 7.57471L13.542 8.42527L7.49999 1.9185Z" fill="#252525"/>
            </svg>
        </a>
    </div>
{{/if }}

